import React, { useEffect, useState } from "react";
import Product from "./Product";
import * as categoryService from "../../services/categoryService";
//import * as productService from "../../services/productService";
//import { Navigate } from "react-router";

function ProductList() {
  const [pageData, setPageData] = useState({
    arrayOfProducts: [],
    productComponents: [],
  });

  const mapProduct = (aProduct) => {
    return (
      <Product product={aProduct} key={"ListA-" + aProduct.id} />
    );
  };

  useEffect(() => {
    categoryService
      .getProductsByCategory()
      .then(onGetProductsSuccess)
      .catch(onGetProductsError);
  }, []);

  const onGetProductsSuccess = (data) => {
    let arrayOfCars = data.items;
    console.log({ arrayOfCars });

    setPageData((prevState) => {
      const pd = { ...prevState };
      pd.arrayOfProducts = arrayOfCars;
      pd.productComponents = arrayOfCars.map(mapProduct);
      return pd;
    });
  };

  const onGetProductsError = (error) => {
    console.error(error);
  };

  return <div className="row">{pageData.productComponents}</div>;
}

export default ProductList;
