import React, { useEffect } from "react";
import useState from "react";
import { useLocation } from "react-router-dom";

function Product() {
  const [cardData, setCardData] = useState({
    id: "",
    make: "",
    name: "",
    price: "",
    Id: "",
  });

  const { state } = useLocation();

  useEffect(() => {
    if (state?.type === "PERSON_VIEW" && state?.payload) {
      setCardData((prevState) => {
        return { ...prevState, ...state.payload };
      });
    }
  }, [state]);

  const product = cardData.products;

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h4 className="font-weight-bold mb-3">{product.name}</h4>
          <p className="font-weight-bold blue-text">{product.make}</p>
          <button className="btn" id={product.id}>
            <i className="fas fa-redo-alt"></i> details
          </button>
        </div>
      </div>
    </div>
  );
}
export default Product;
