import React, { useEffect, useState } from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import CategoryIndex from "./components/categories/CategoryIndex";
import ProductIndex from "./components/products/ProductIndex";
import ProductDetailPage from "./components/products/ProductDetailPage";
import * as categoryService from "./services/categoryService";
import * as productService from "./services/productService";

function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [categoryId, setCategoryId] = useState(null);
  const [productId, setProductId] = useState(null);

  useEffect(() => {
    console.log("firing useEffect for getCategories");
    categoryService
      .getCategories()
      .then((res) => setCategories(res.items))
      .catch((err) => console.error(err));
  }, []);

  useEffect(() => {
    if (categoryId) {
      productService
       .getProductByCategory(categoryId)
       .then((res) => setProducts(res))
       .catch((err) => console.error(err));
    }
  }, [categoryId]);

  useEffect(() => console.log(productId))

  const HandleCategoryChange = (id) => {
    setCategoryId(id)
    useNavigate(`/category/${categoryId}`)
  }

  return (
    <React.Fragment>
      <Routes>
        <Route
          path="/category"
          element={
            <CategoryIndex
              categories={categories}
              setCategoryId={HandleCategoryChange}
            />
          }
        ></Route>
        <Route
          path="/category/:id"
          element={
            <ProductIndex
              products={products}
              setProductId={setProductId}
            />
          }
        ></Route>
        <Route
          path="/product/:id"
          element={<ProductDetailPage />}
        ></Route>
      </Routes>
    </React.Fragment>
  );
}

export default App;
