import React from "react";
import { useNavigate } from "react-router-dom";

import debug from "sabio-debug";

const _logger = debug.extend("Category");

function Category(props) {
  const aCategory = props.category;
  _logger(props.category);
  const navigate = useNavigate();

  const onCategoryClicked = () => {
    _logger("view category", aCategory);
    const state = { type: "PERSON_VIEW", payload: aCategory };
    navigate(`/category/${aCategory.id}`, { state });
  };

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{aCategory.name}</h5>
          <button
            id={aCategory.id}
            className="link-btn btn btn-danger"
            onClick={onCategoryClicked}
          >
            View
          </button>
        </div>
      </div>
    </div>
  );
}

export default Category;
