import React, { useEffect, useState } from "react";
import Category from "./CategoryTemplate";
import * as categoryService from "../../services/categoryService";

function CategoryIndex() {
  const [pageData, setPageData] = useState({
    arrayOfCategories: [],
    categoryComponents: [],
  });

  const mapCategory = (singleCategory) => {
    return (
      <Category
        category={singleCategory}
        key={"ListA-" + singleCategory.id}
      />
    );
  };

  useEffect(() => {
    console.log("firing useEffect for getCategories");
    categoryService
      .getCategories()
      .then(onGetCategoriesSuccess)
      .catch(onGetCategoriesError);
  }, []);

  const onGetCategoriesSuccess = (data) => {
    let arrayOfAisles = data.items;
    console.log({ arrayOfAisles });

    setPageData((prevState) => {
      const pd = { ...prevState };
      pd.arrayOfCategories = arrayOfAisles;
      pd.categoryComponents = arrayOfAisles.map(mapCategory);
      return pd;
    });
  };

  const onGetCategoriesError = (error) => {
    console.error(error);
  };

  return <div className="row">{pageData.categoryComponents}</div>;
}

export default CategoryIndex;
