import React, { useEffect } from "react";
import useState from "react";
import { useLocation } from "react-router-dom";

function Product() {
  const [cardData, setCardData] = useState({
    id: "",
    make: "",
    name: "",
    price: "",
    Id: "",
  });

  const { state } = useLocation();

  useEffect(() => {
    if (state?.type === "PERSON_VIEW" && state?.payload) {
      setCardData((prevState) => {
        return { ...prevState, ...state.payload };
      });
    }
  }, [state]);

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h4 className="font-weight-bold mb-3">{cardData.name}</h4>
          <p className="font-weight-bold blue-text">
            {cardData.make}
          </p>
          <button className="btn" id={cardData.id}>
            <i className="fas fa-redo-alt"></i> details
          </button>
        </div>
      </div>
    </div>
  );
}
export default Product;
