import React, { useEffect, useState } from "react";
import Category from "./Category";
import categoryService from "../../services/categoryService";
//import { Navigate } from "react-router";

function CategoryHome() {
  const [pageData, setPageData] = useState({
    arrayOfCategories: [],
    categoryComponents: [],
  });

  const mapCategory = (aCategory) => {
    return (
      <Category category={aCategory} key={"ListA-" + aCategory.id} />
    );
  };

  useEffect(() => {
    categoryService
      .getCategories()
      .then(onGetCategoriesSuccess)
      .catch(onGetCategoriesError);
  }, []);

  const onGetCategoriesSuccess = (data) => {
    let arrayOfMakes = data.item.pagedItems;

    setPageData((prevState) => {
      const pd = { ...prevState };
      pd.arrayOfCategories = arrayOfMakes;
      pd.categoryComponents = arrayOfMakes.map(mapCategory);
      return pd;
    });
  };

  const onGetCategoriesError = (error) => {
    console.error(error);
  };

  return <div className="row">{pageData.categoryComponents}</div>;
}

export default CategoryHome;
