import React from "react";
import { useNavigate } from "react-router-dom";
import { PropTypes } from "react";

import debug from "sabio-debug";

const _logger = debug.extend("Person");

function Category(props) {
  const aCategory = props.category;
  _logger(props.category);
  const navigate = useNavigate();

  const onNavToProductList = (evt) => {
    evt.PreventDefault();
    props.onCategoryClicked(aCategory, evt);
    navigate(`/product/${aCategory.id}`);
  };

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{aCategory.name}</h5>
          <button
            id={aCategory.id}
            className="link-btn btn btn-danger"
            onClick={onNavToProductList}
          >
            View
          </button>
        </div>
      </div>
    </div>
  );
}

Category.propTypes = {
  category: PropTypes.shape({
    name: PropTypes.string.isRequired,
  }),
};

export default Category;
