import React from "react";
import * as categoryService from "../../services/categoryService";

import debug from "sabio-debug";

const _logger = debug.extend("Category");

function Category(props) {
  const aCategory = props.category;
  _logger(props.category);

  const onCategoryClicked = () => {
    categoryService
      .getProductsByCategory()
      .then(onGetProductsSucccess)
      .catch(onGetProductsError);
  };

  const onGetProductsSucccess = (data) => {
    _logger(data.items);
  };

  const onGetProductsError = (error) => {
    _logger.error("onGetProductsError", error);
  };

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{aCategory.name}</h5>
          <button
            id={aCategory.id}
            className="link-btn btn btn-danger"
            onClick={onCategoryClicked}
          >
            View
          </button>
        </div>
      </div>
    </div>
  );
}

export default Category;
