import React from "react";
import * as productService from "../../services/productService";
import debug from "sabio-debug";

function Product(props) {
  const _logger = debug.extend("Product");
  const singleProduct = props.item;
  _logger(props.item);

  const onProductClicked = () => {
    productService
      .getProductById()
      .then(onGetProductSuccess)
      .catch(onGetProductError);
  };

  const onGetProductSuccess = () => {};

  const onGetProductError = (error) => {
    _logger.error("onGetProductError", error);
  };

  return (
    <div className="col-md-3">
      <div className="card">
        <div className="card-body">
          <h4 className="font-weight-bold mb-3">
            {singleProduct.name}
          </h4>
          <p className="font-weight-bold blue-text">
            {singleProduct.make}
          </p>
          <button
            className="btn"
            id={singleProduct.id}
            onClick={onProductClicked}
          >
            details
          </button>
        </div>
      </div>
    </div>
  );
}
export default Product;
