import React, { useEffect, useState } from "react";
import Product from "./ProductTemplate";
//import * as categoryService from "../../services/categoryService";
//import Category from "../categories/Category";
//import * as productService from "../../services/productService";
//import { Navigate } from "react-router";

function ProductIndex() {
  const [pageData, setPageData] = useState({
    arrayOfProducts: [],
    productComponents: [],
  });

  const mapProduct = (aProduct) => {
    return (
      <Product product={aProduct} key={"ListA-" + aProduct.id} />
    );
  };

  useEffect(() => {
    Product(pageData);
    let arrayOfItems = pageData.items.product;
    console.log({ arrayOfItems });

    setPageData((prevState) => {
      const pd = { ...prevState };
      pd.arrayOfProducts = arrayOfItems;
      pd.productComponents = arrayOfItems.map(mapProduct);
      return pd;
    });
  }, []);

  return <div className="row">{pageData.productComponents}</div>;
}

export default ProductIndex;
