import React, { useEffect } from "react";
import useState from "react";
import { useLocation } from "react-router-dom";

function Product() {
  const [cardData, setCardData] = useState({
    arrayOfProducts: [],
  });

  const { state } = useLocation();

  useEffect(() => {
    if (state?.type === "PERSON_VIEW" && state?.payload) {
      setCardData((prevState) => {
        return { ...prevState, ...state.payload };
      });
    }
  }, [state]);

  return (
    <div className="col-md-3">
      <div className="card-wrapper">
        <div id="card-1" className="card card-rotating text-center">
          <div className="face front">
            <div className="card-up">
              <img
                className="card-img-top"
                src={cardData.ImageUrl}
                alt="..."
              />
            </div>

            <div className="card-body">
              <h4 className="font-weight-bold mb-3">
                {cardData.Name}
              </h4>
              <p className="font-weight-bold blue-text">
                {cardData.Make}
              </p>

              <button className="rotate-btn" data-card="card-1">
                <i className="fas fa-redo-alt"></i> Click here to
                rotate
              </button>
            </div>
          </div>

          <div className="face back">
            <div className="card-body">
              <h4 className="font-weight-bold mb-0">
                {cardData.Price}
              </h4>
              <hr />
              <p>{cardData.Description}</p>
              <hr />

              <button className="rotate-btn" data-card="card-1">
                <i class="fas fa-undo"></i> Click here to rotate back
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Product;
